        private const char UP = '(';
        private const char DOWN = ')';
        
        static void Main(string[] args)
        {
            int floor = 0;
            int firstBasementPosition = 0;
            bool basementReached = false;

            for(int i = 0; i < _input.Length; i++)
            {
                char command = _input[i];
                if (command == UP)
                {
                    floor++;
                }
                else if (command == DOWN)
                {
                    floor--;
                }

                if (!basementReached)
                {
                    if (floor == -1)
                    {
                        basementReached = true;
                        firstBasementPosition = i + 1;
                    }
                }
            }

            Console.WriteLine("Final floor: " + floor);
            if (basementReached)
            {
                Console.WriteLine("Basement reached at position: " + firstBasementPosition);
            }
            else
            {
                Console.WriteLine("Basement never reached!");
            }
            Console.ReadKey();
